import React from 'react';
import { Container, Nav, Navbar, NavDropdown, Button } from 'react-bootstrap';
import bloodNavbar from '../assets/images/bloodNavbar.png';
import './Navbar.css';
import { Link, useNavigate } from 'react-router-dom';

function NavigationBar() {
  const navigate = useNavigate();
  const isAuthenticated = localStorage.getItem('userId');
  const userType = localStorage.getItem('userType');

  const handleLogout = () => {
    localStorage.removeItem('userId');
    localStorage.removeItem('userType');
    navigate('/login');
  };

  return (
    <Navbar bg="light" expand="lg" className="shadow-sm">
      <Container>
        <Navbar.Brand as={Link} to="/">
          <img
            src={bloodNavbar}
            alt="Gota de sangue"
            className="img-fluid"
            style={{ 
              maxWidth: '50px',
              filter: 'brightness(2) contrast(1.5) saturate(1.2)'
            }}
          />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav" className="justify-content-end">
          <Nav className="align-items-start">
            <Nav.Link as={Link} to="/" className="mx-2" active>Home</Nav.Link>
            <Nav.Link as={Link} to="/sobre" className="mx-2">Sobre n√≥s</Nav.Link>
            <Nav.Link as={Link} to="/necessidades" className="mx-2">Encontrar Sangue</Nav.Link>
            {userType === 'banco' && (
              <Nav.Link as={Link} to="/gerenciar-necessidades" className="mx-2">Gerenciar Necessidades</Nav.Link>
            )}
            <NavDropdown 
              title="Cadastre-se" 
              id="basic-nav-dropdown" 
              className="mx-2"
            >
              <NavDropdown.Item as={Link} to="/cadastro/doador">Seja um doador</NavDropdown.Item>
              <NavDropdown.Item as={Link} to="/cadastro/banco">Cadastrar banco</NavDropdown.Item>
            </NavDropdown>
            {!isAuthenticated ? (
              <Button 
                variant="outline-dark" 
                as={Link} 
                to="/login" 
                className="mx-2 px-4"
                style={{
                  borderRadius: '4px',
                  padding: '0.5rem 1.5rem'
                }}
              >
                Entrar
              </Button>
            ) : (
              <Button 
                variant="outline-danger" 
                onClick={handleLogout}
                className="mx-2 px-4"
                style={{
                  borderRadius: '4px',
                  padding: '0.5rem 1.5rem'
                }}
              >
                Sair
              </Button>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavigationBar;
