import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3000'
});

export const registrarBancoSangue = async (dados) => {
  try {
    const response = await api.post('/cadastro/bancos-sangue/registro', dados);
    return response.data;
  } catch (error) {
    console.error('Erro na API:', error);
    throw error;
  }
};

export const registrarDoador = async (dados) => {
  try {
    const response = await api.post('/cadastro/doadores/registro', dados);
    return response.data;
  } catch (error) {
    console.error('Erro na API:', error);
    throw error;
  }
};

export default api;
